DROP TABLE IF EXISTS `ims_bm_attraction_classify`; CREATE TABLE `ims_bm_attraction_classify` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `sort` int(11) NOT NULL, `spottitle` varchar(30) NOT NULL, `department_id` int(11) NOT NULL, `spotdesc` varchar(100) NOT NULL, `spotpic` varchar(200) NOT NULL, `spotinfo` varchar(500) NOT NULL, `spotrecord` varchar(200) NOT NULL, `spotcolor` varchar(100) NOT NULL, `spottime` varchar(100) NOT NULL, `spotdistance` varchar(10) NOT NULL, `spoturl` varchar(100) NOT NULL, `spotsmallpic` varchar(200) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `ims_bm_attraction_other`; CREATE TABLE `ims_bm_attraction_other` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `sort` int(11) NOT NULL, `spottitle` varchar(30) NOT NULL, `department_id` int(11) NOT NULL, `spotdesc` varchar(100) NOT NULL, `spotpic` varchar(200) NOT NULL, `spotinfo` varchar(500) NOT NULL, `spotrecord` varchar(200) NOT NULL, `spotcolor` varchar(100) NOT NULL, `spottime` varchar(100) NOT NULL, `spotdistance` varchar(10) NOT NULL, `spoturl` varchar(100) NOT NULL, `spotsmallpic` varchar(200) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `ims_bm_attraction_reply`; CREATE TABLE `ims_bm_attraction_reply` ( `id` int(11) NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL DEFAULT '0', `weid` int(11) NOT NULL, `title` varchar(30) NOT NULL, `picurl` varchar(500) NOT NULL, `info` varchar(500) NOT NULL, `department` varchar(100) NOT NULL, `gonglueurl` varchar(200) NOT NULL, `gonglue` varchar(1000) NOT NULL, `imageurl` varchar(200) NOT NULL, `imageinfo` varchar(1000) NOT NULL, `map` varchar(100) NOT NULL, `mapdesc` varchar(100) NOT NULL, `detail` varchar(10000) NOT NULL, `startdesc` varchar(100) NOT NULL, `startrecord` varchar(200) NOT NULL, `startinfo` varchar(1000) NOT NULL, `lng` decimal(10,2) DEFAULT '0.00', `lat` decimal(10,2) DEFAULT '0.00', `telephone` varchar(1000) NOT NULL, `spoturl` varchar(100) DEFAULT '', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `ims_bm_attraction_slide`; CREATE TABLE `ims_bm_attraction_slide` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) DEFAULT NULL, `hs_pic` text COMMENT '幻灯图片', `createtime` int(11) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
