CREATE TABLE IF NOT EXISTS `ims_hao_recommend_list` ( `id` int(11) NOT NULL AUTO_INCREMENT PRIMARY KEY, `category` int(11) NOT NULL, `username` varchar(225) NOT NULL COMMENT '用户名', `phone` varchar(225) NOT NULL COMMENT '手机号', `publicname` varchar(225) NOT NULL COMMENT '公众号名称', `publicdescription` varchar(225) NOT NULL COMMENT '公众号简述', `publicimage` varchar(225) NOT NULL COMMENT '公众号二维码', `icon` varchar(225) NOT NULL COMMENT '小图标', `status` int(11) NOT NULL COMMENT '审核状态', `time` varchar(225) NOT NULL COMMENT '提交时间', `hit` int(11) NOT NULL COMMENT '热度', `openid` varchar(225) NOT NULL COMMENT 'openid', `uniacid` int(10) NOT NULL COMMENT 'uniacid', `sort` int(11) NOT NULL COMMENT 'sort' ) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_hao_recommend_setting` ( `id` int(11) NOT NULL AUTO_INCREMENT PRIMARY KEY, `uniacid` int(10) NOT NULL COMMENT '公众号ID', `image` varchar(255) NOT NULL COMMENT '推广二维码', `tempID` varchar(255) NOT NULL COMMENT '模板ID' ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_hao_recommend_banner` ( `id` int(11) NOT NULL AUTO_INCREMENT PRIMARY KEY, `image` varchar(255) NOT NULL, `link` varchar(300) NOT NULL, `sort` varchar(255) NOT NULL, `display` int(11) NOT NULL, `uniacid` int(10) NOT NULL ) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;
