CREATE TABLE IF NOT EXISTS `ims_haoman_scene_addad` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) NOT NULL DEFAULT '0', `uniacid` int(10) unsigned NOT NULL COMMENT '公众号ID', `rulename` varchar(255) NOT NULL COMMENT '活动规则', `adlogo` varchar(255) NOT NULL DEFAULT '0' COMMENT '广告图片', `adtitle` varchar(255) NOT NULL DEFAULT '0' COMMENT '广告标题', `addetails` varchar(255) NOT NULL DEFAULT '0' COMMENT '广告简介', `adlink` varchar(255) NOT NULL DEFAULT '0' COMMENT '广告链接', `createtime` int(11) NOT NULL, `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0表示未投放，1表示投放', PRIMARY KEY (`id`), KEY `idx_id_rulename` (`id`,`rulename`) USING BTREE ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
