$(".aHeaderSearch")[0] && $(".aHeaderSearch").click(function(e) {
    if ($(".searchPop").css("display") == "none") {
        $("html,body").css({
            "overflow-y": "hidden"
        });
        $(".searchPop").show();
    } else {
        $("html,body").css({
            "overflow-y": "auto"
        });
        $(".searchPop").hide();
    }
});


$(".aUnLogin")[0] && $(".aUnLogin").click(function(e) {
    $("#index_menu_box").hide();
    $(".index_menu_top").hide();
    $(".ulHotList").hide();
    $(".loginPop").hide();
    $("#search_box").hide();
    $(".headRight a").removeClass("cur");
    if ($("#ls_box").css("display") == "none") {
        $("html,body").css({
            "overflow-y":"hidden"
        });
        $(".aUnLogin").addClass("cur");
        $("#ls_box").show();
    } else {
        searchM.addCurClass();
        $("html,body").css({
            "overflow-y":"auto"
        });
        $("#ls_box").hide();
        $("#hot_word_box").hide();
        $("#mask_box").hide();
    }
});


$(".aNav")[0] && $(".aNav").click(function() {
    if (!$(".searchForm")[0]) {
        $("#search_box").hide();
    }
    $(".ulHotList")[0] && $(".ulHotList").hide();
    $(".loginPop").hide();
    $(".headRight a").removeClass("cur");
    if ($(".index_menu_top")[0]) {
        $("#mask_box").hide();
        if ($(".index_menu_top").css("display") == "none") {
            $(".index_menu_top").show();
            $("#nav_menu .con").css({
                width:""
            });
            var width = $(".subNav p").width() + 30;
            $("#nav_menu .con").css({
                width:width
            });
            indexMenu.refresh();
        } else {
            searchM.addCurClass();
            $(".index_menu_top").hide();
        }
    } else {
        if ($("#index_menu_box").css("display") == "none") {
            $("html,body").css({
                "overflow-y":"hidden"
            });
            $(".aNav").addClass("cur");
            $("#mask_box").show();
            $("#index_menu_box").show();
        } else {
            searchM.addCurClass();
            $("html,body").css({
                "overflow-y":"auto"
            });
            $("#index_menu_box").hide();
            $("#mask_box").hide();
        }
    }
});

if ($("#search_menu")[0]) {
    var width = $(".searchNav p").width() + 30;
    $("#search_menu .con").css({
        width:width
    });
    var searchMenu = new iScroll("search_menu", {
        hScroll:true,
        vScroll:false,
        bounce:true,
        momentum:true
    });
    $(window).on("resize", function() {
        $("#search_menu .con").css({
            width:""
        });
        var width = $(".searchNav p").width() + 30;
        $("#search_menu .con").css({
            width:width
        });
        searchMenu.refresh();
    });
    if ($("#search_menu a.cur")[0]) {
        var screenWidth = $(".header").width();
        var leftWidth = $("#search_menu a.cur").offset().left;
        var aWidth = $("#search_menu a.cur").width();
        var scrollLeft = parseInt(screenWidth - leftWidth - aWidth);
        if (scrollLeft < 5) {
            searchMenu.scrollTo(-screenWidth);
        }
    }
}



$(window).scroll(function() {
    var num = $(this).scrollTop();
    if (num > 100) {
        $(".fixedBtnList").show();
    } else {
        $(".fixedBtnList").hide();
    }
});

$(".aFeedBack").click(function() {
    var param = escape(window.location.href);
    var url = $(this).attr("href") + "?from=" + param;
    $(this).attr("href", url);
});