DROP TABLE IF EXISTS `ims_jer_live`; CREATE TABLE `ims_jer_live` ( `id` mediumint(10) NOT NULL AUTO_INCREMENT, `uniacid` mediumint(10) DEFAULT NULL, `rid` mediumint(10) DEFAULT '0', `name` varchar(255) DEFAULT NULL, `thumb` varchar(255) DEFAULT NULL, `description` varchar(400) DEFAULT NULL, `view` int(10) DEFAULT '0', `message_add_status` tinyint(2) DEFAULT '0', `comment_add_status` tinyint(2) DEFAULT '0', `sort` int(10) DEFAULT '0', `live_share_title` varchar(255) DEFAULT NULL, `live_share_desc` varchar(255) DEFAULT NULL, `live_share_img` varchar(255) DEFAULT NULL, `live_type` tinyint(2) DEFAULT NULL, `live_charge` tinyint(2) DEFAULT NULL, `live_charge_price` varchar(20) DEFAULT NULL, `live_video` text, `live_tab` text, `live_marquee_status` tinyint(2) DEFAULT NULL, `live_marquee_content` text, `live_top_ad_img` varchar(255) DEFAULT NULL, `live_top_ad_img_link` varchar(255) DEFAULT NULL, `createtime` int(10) DEFAULT NULL, `starttime` int(10) DEFAULT NULL, `status` tinyint(2) NOT NULL DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
