CREATE TABLE IF NOT EXISTS `ims_daojia_address` ( `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID', `uniacid` int(11) NOT NULL DEFAULT '0' COMMENT '公众号ID', `uid` int(11) NOT NULL DEFAULT '0' COMMENT '会员ID', `name` varchar(255) NOT NULL DEFAULT '' COMMENT '姓名', `phone` varchar(50) DEFAULT NULL COMMENT '电话', `province` varchar(50) DEFAULT NULL COMMENT '省份', `city` varchar(50) DEFAULT NULL COMMENT '城市', `district` varchar(50) DEFAULT NULL COMMENT '地区', `address` varchar(600) DEFAULT NULL COMMENT '地址', `is_default` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否为默认地址', `orderby` int(11) NOT NULL DEFAULT '0' COMMENT '排序', `create_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '创建时间', `update_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '修改时间', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
