CREATE TABLE IF NOT EXISTS `ims_daojia_cart` ( `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID', `uniacid` int(11) NOT NULL DEFAULT '0' COMMENT '公众号ID', `uid` int(11) NOT NULL DEFAULT '0' COMMENT '会员ID', `session_id` varchar(255) NOT NULL DEFAULT '' COMMENT 'session_id', `goods_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品ID', `goods_name` varchar(255) NOT NULL DEFAULT '' COMMENT '商品名称', `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品价格', `buy_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '实际价格', `buy_num` int(11) NOT NULL DEFAULT '0' COMMENT '购买数量', `discount` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '折扣价格', `daojia` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否到家服务', `params` text COMMENT '其他参数', `create_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '加入时间', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
