CREATE TABLE IF NOT EXISTS `ims_daojia_order` ( `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID', `uniacid` int(11) NOT NULL DEFAULT '0' COMMENT '公众号ID', `uid` int(11) NOT NULL DEFAULT '0' COMMENT '会员ID', `order_sn` varchar(255) NOT NULL DEFAULT '' COMMENT '订单编号', `daojia` tinyint(1) DEFAULT NULL COMMENT '是否到家', `contact_name` varchar(50) DEFAULT NULL COMMENT '联系人', `phone` varchar(100) DEFAULT NULL COMMENT '联系电话', `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '价格', `discount` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '折扣价格', `buy_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '实际价格', `daojia_fee` decimal(8,2) NOT NULL, `service_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '服务状态', `payment_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '付款状态', `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '订单状态', `params` text COMMENT '参数', `address_id` int(11) NOT NULL DEFAULT '0' COMMENT '地址ID', `staff_id` int(11) NOT NULL DEFAULT '0' COMMENT '美容师的ID', `yuyue_time` varchar(255) DEFAULT NULL COMMENT '预约的时间', `memo` varchar(600) DEFAULT NULL COMMENT '订单备注', `create_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '创建时间', `update_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '修改时间', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_daojia_order_item` ( `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID', `uniacid` int(11) NOT NULL DEFAULT '0' COMMENT '公众号ID', `uid` int(11) NOT NULL DEFAULT '0' COMMENT '会员ID', `order_id` int(11) NOT NULL DEFAULT '0' COMMENT '订单ID', `goods_id` int(11) NOT NULL DEFAULT '0' COMMENT '商品ID', `goods_name` varchar(255) NOT NULL DEFAULT '' COMMENT '商品名称', `daojia` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否上门1/0', `buy_num` int(11) NOT NULL DEFAULT '0' COMMENT '购买数量', `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '单价', `discount` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '折扣价格', `price_total` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '总价', `price_total_real` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '实际总价（减去折扣之后）', `params` text, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_daojia_store` ( `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID', `uniacid` int(11) NOT NULL DEFAULT '0' COMMENT '公众号ID', `title` varchar(255) NOT NULL DEFAULT '' COMMENT '名称', `photo` varchar(255) DEFAULT NULL COMMENT '缩略图', `province` varchar(255) DEFAULT NULL COMMENT '省', `city` varchar(255) DEFAULT NULL COMMENT '市', `district` varchar(255) DEFAULT NULL COMMENT '县区', `address` varchar(600) DEFAULT NULL COMMENT '地址', `lng` varchar(100) DEFAULT NULL COMMENT '经度', `lat` varchar(100) DEFAULT NULL COMMENT '纬度', `tel` varchar(100) DEFAULT NULL COMMENT '电话', `contact_name` varchar(100) DEFAULT NULL COMMENT '联系人', `post_daojia_fee` decimal(8,2) NOT NULL, `free_daojia_fee_reach` decimal(8,2) NOT NULL, `status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否启用', `yuyue_after_minutes` int(8) NOT NULL COMMENT '可预约起始时间', `begin_hour` int(2) NOT NULL, `end_hour` int(2) NOT NULL, `create_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '创建时间', `update_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:01' COMMENT '修改时间', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_daojia_user` ( `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID', `uniacid` int(11) NOT NULL DEFAULT '0' COMMENT '公众号ID', `member_id` int(11) NOT NULL DEFAULT '0' COMMENT '系统会员表ID', `user_type` varchar(255) NOT NULL DEFAULT '0' COMMENT '会员类型', `online` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否在线1/0', `staff` text COMMENT '美容师的资料encode', `staff_name` varchar(255) DEFAULT NULL COMMENT '美容师的姓名', `staff_store_id` int(11) NOT NULL DEFAULT '0' COMMENT '门店', `staff_work_no` varchar(255) DEFAULT NULL COMMENT '工号', `staff_photo` varchar(255) DEFAULT NULL COMMENT '照片', `staff_phone` varchar(255) DEFAULT NULL COMMENT '电话', `staff_desc` text COMMENT '自我介绍', PRIMARY KEY (`id`), KEY `uniacid` (`uniacid`), KEY `member_id` (`member_id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
