manifest.install:
	DROP TABLE IF EXISTS `ims_xsy_wxfile`;
CREATE TABLE `ims_xsy_wxfile` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uniacid` int(11) DEFAULT NULL,
  `name` varchar(255) DEFAULT '',
  `createtime` int(11) DEFAULT NULL COMMENT '收入时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='文件';
