DROP TABLE IF EXISTS `ims_zofui_hby_activity`; CREATE TABLE `ims_zofui_hby_activity` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT NULL, `lastmoney` decimal(6,2) DEFAULT NULL, `time` varchar(64) DEFAULT NULL, `money` decimal(6,2) DEFAULT NULL, PRIMARY KEY (`id`), KEY `inx_uniacid` (`uniacid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `ims_zofui_hby_hblog`; CREATE TABLE `ims_zofui_hby_hblog` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT NULL, `actid` int(11) DEFAULT NULL, `openid` varchar(120) DEFAULT NULL, `money` decimal(3,2) DEFAULT '0.00', `time` varchar(64) DEFAULT NULL, PRIMARY KEY (`id`), KEY `inx_actid` (`actid`), KEY `inx_openid` (`openid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `ims_zofui_hby_user`; CREATE TABLE `ims_zofui_hby_user` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT NULL, `actid` int(11) DEFAULT NULL, `parentid` int(11) DEFAULT NULL, `openid` varchar(120) DEFAULT NULL, `nickname` varchar(64) DEFAULT NULL, `headimgurl` varchar(500) DEFAULT NULL, `time` varchar(64) DEFAULT NULL, `times` int(5) DEFAULT '-1' COMMENT '点击次数', `subscribe` int(1) DEFAULT NULL, PRIMARY KEY (`id`), KEY `inx_uniacid` (`uniacid`), KEY `inx_parentid` (`parentid`), KEY `inx_openid` (`openid`), KEY `inx_actid` (`actid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
